import './App.css';
import React, {useEffect, useState} from 'react';
import {BrowserRouter as Router, Route, Switch, useHistory, Link} from "react-router-dom";
import Prueba from './Prueba';


function Body() {
  function clear() {
    document.getElementById('5wform').reset();
  }
  function searchWebs() {
    var purpose = document.getElementById("purpose").value;
    var how = document.getElementById("how").value;
    var why = document.getElementById("why").value;
    var what = document.getElementById("what").value;
    var where = document.getElementById("where").value;
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Web extension market analysis</h1>
      </header>
      <div className="extension-expl">
        <h2>Extension Analysis</h2>
        <p>Introduce the desired tags of the Chrome extension below, you can left blanks tags (except the purpose one). After that, click on "Search", select the desired query, and follow the steps.</p>
      </div>
      <table className="center">
        <tbody>
          <tr>
            <th>Purpose (*)</th>
            <th>How</th>
            <th>Why</th>
            <th>What</th>
            <th>Where</th>
          </tr>
          <tr>
            <td><input id="purpose"></input></td>
            <td><input id="how"></input></td>
            <td><input id="why"></input></td>
            <td><input id="what"></input></td>
            <td><input id="where"></input></td>
          </tr>
        </tbody>
      </table>
      <div className="analysis-buttons">
        <button onClick={clear}>Clear</button>
        <button onClick={searchWebs}>Search</button>
      </div>
      <div>
      </div>

    </div>
  );
}

function App() {

    return (
        <Router>
            <Route path="/" component={Body}/>
        </Router>
    );
}

export default App;
